import { useEffect, useState } from "react";
import CommentSection from "../components/CommentSection";

function Feed() {
  const [posts, setPosts] = useState([]);
  const [newPost, setNewPost] = useState("");

  const access = localStorage.getItem("access");

  useEffect(() => {
    fetch("http://localhost:8000/api/posts/", {
      headers: { Authorization: "Bearer " + access }
    })
      .then(res => res.json())
      .then(data => setPosts(data));
  }, []);

  const handlePost = () => {
    if (!newPost.trim()) return;

    fetch("http://localhost:8000/api/posts/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + access
      },
      body: JSON.stringify({ content: newPost })
    })
      .then(res => res.json())
      .then(data => {
        alert(data.message);
        // Yeni postu geÃ§ici bir ID ile en Ã¼ste ekle
        setPosts([
          {
            id: Math.random(), // geÃ§ici id
            user: "You",
            content: newPost,
            created_at: new Date().toISOString()
          },
          ...posts
        ]);
        setNewPost("");
      });
  };

  return (
    <div>
      <h2>ðŸ“° Feed</h2>

      <textarea
        placeholder="What's on your mind?"
        value={newPost}
        onChange={(e) => setNewPost(e.target.value)}
      /><br />
      <button onClick={handlePost}>Post</button>

      <hr />

{posts.map((post, index) => (
  <div key={index} style={{ border: "1px solid #ccc", margin: "10px", padding: "10px" }}>
    <strong>{post.user}</strong><br />
    <p>{post.content}</p>
    <small>{new Date(post.created_at).toLocaleString()}</small>

    {/* Sadece kendi postun iÃ§in delete gÃ¶ster */}
    {post.user === "You" && (
      <button
        onClick={() => handleDelete(post.id)}
        style={{ backgroundColor: "red", color: "white", marginTop: "5px" }}
      >
        ðŸ—‘ Delete
      </button>
    )}

    {/* ðŸ’¬ Yorumlar */}
    {post.id && <CommentSection postId={post.id} />}
  </div>
))}

    </div>
  );
}

export default Feed;
