import { useEffect, useState } from "react";

function JobList() {
  const [jobs, setJobs] = useState([]);
  const [profile, setProfile] = useState(null);

  useEffect(() => {
    const access = localStorage.getItem("access");

    // KullanÄ±cÄ± profilini Ã§ek
    if (access) {
      fetch("http://localhost:8000/api/profile/", {
        headers: {
          Authorization: "Bearer " + access,
        },
      })
        .then((res) => res.json())
        .then((data) => setProfile(data));
    }

    // Job ilanlarÄ±nÄ± Ã§ek
    fetch("http://localhost:8000/api/job-list/")
      .then((res) => res.json())
      .then((data) => setJobs(data));
  }, []);


  const [role, setRole] = useState("");

useEffect(() => {
  fetch("http://localhost:8000/api/profile/", {
    headers: { Authorization: "Bearer " + localStorage.getItem("access") }
  })
    .then(res => res.json())
    .then(data => setRole(data.role));
}, []);

  const handleFlag = (jobId) => {
  fetch(`http://localhost:8000/api/jobs/${jobId}/flag/`, {
    method: "POST",
    headers: {
      Authorization: "Bearer " + localStorage.getItem("access")
    }
  })
    .then(res => res.json())
    .then(data => alert(data.message));
};

  // BaÅŸvuru gÃ¶nderme fonksiyonu
  const handleApply = (jobId) => {
    const access = localStorage.getItem("access");

    fetch("http://localhost:8000/api/apply/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + access,
      },
      body: JSON.stringify({ job_id: jobId }),
    })
      .then((res) => res.json())
      .then((data) => {
        alert(data.message);
      });
  };

  return (
    <div>
      <h2>Job Listings</h2>
      {jobs.length === 0 && <p>No jobs available.</p>}
      {jobs.map((job) => (
        <div
          key={job.id}
          style={{
            border: "1px solid #ccc",
            margin: "10px",
            padding: "10px",
          }}
        >
          <h3>{job.title}</h3>
          <p>
            <strong>Company:</strong> {job.company}
          </p>
          <p>
            <strong>Location:</strong> {job.location}
          </p>
          <p>{job.description}</p>
          <p>
            <em>Posted by {job.posted_by}</em>
          </p>

          {/* Sadece 'user' rolÃ¼ iÃ§in Apply butonu */}
          {profile?.role === "user" && (
  <>
    <button onClick={() => handleApply(job.id)}>Apply</button>
    <button onClick={() => handleFlag(job.id)}>ðŸš© Report Job</button>
  </>
)}
        </div>
      ))}
    </div>
  );
}

export default JobList;
