import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";


function Profile() {
  const [data, setData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const access = localStorage.getItem("access");
    if (access) {
      fetch("http://localhost:8000/api/profile/", {
        headers: {
          Authorization: "Bearer " + access
        }
      })
        .then(res => res.json())
        .then(profile => {
          setData(profile);
          // √∂rnek otomatik y√∂nlendirme (isteƒüe baƒülƒ±):
          // if (profile.role === "admin") navigate("/admin");
        });
    }
  }, []);

  if (!data) return <p>Loading...</p>;

  return (
      <div>
        <h2>Welcome, {data.username}</h2>
        <p>Your role: {data.role}</p>
        {data.bio && <p><strong>Bio:</strong> {data.bio}</p>}
        {data.experience && <p><strong>Experience:</strong> {data.experience}</p>}
        {data.education && <p><strong>Education:</strong> {data.education}</p>}
        <div>
          <h2>Welcome, {data.username}</h2>
          <p>Your role: {data.role}</p>

          <button onClick={() => {
            localStorage.removeItem("access");
            navigate("/");
          }}>
            üö™ Logout
          </button>
        </div>


        {data.role === "user" && (
            <div>
              <p>üßç You can apply to jobs.</p>
            </div>
        )}

        {data.role === "recruiter" && (
            <div>
              <p>üè¢ You can post new jobs.</p>
            </div>
        )}

        {data.role === "admin" && (
            <div>
              <p>üõ† You have admin access.</p>
              <Link to="/admin">üõ† Go to Admin Panel</Link>
            </div>
        )}
        {/* üîó Rol bazlƒ± linkler */}
        {data.role === "user" && (
            <div>
              <Link to="/jobs">üîç View Job Listings</Link>
              <Link to="/edit-profile">‚úèÔ∏è Edit Profile</Link>
              <Link to="/users">üîó Find People</Link>
              <Link to="/connections">üîó Incoming Requests</Link>
              <Link to="/my-connections">üë• My Connections</Link>


            </div>
        )}

        {data.role === "recruiter" && (
            <div>
              <Link to="/post-job">üì¢ Post a Job</Link><br/>
              <Link to="/jobs">üìÑ View All Jobs</Link>
              <Link to="/applications">üì• View Applications</Link>
            </div>
        )}
      </div>
  );
}


export default Profile;
